import './App.css';
import Connection from "./components/MetaMaskConnection";
import ContractPage from "./components/ContractPage";
import { useState } from "react";
import { ethers } from "ethers";
import Conabi from "./abi/SimpleCounter.json";
import ConAdd from "./abi/ContractAddress.json";

const contractAbi = Conabi.abi;
const contractAddress = ConAdd.address;

function App() {

  const [connected, setConnected] = useState(false);

  const connectMetaMask = async () => {
    let provider = window.ethereum;
    if(provider){
      //MetaMask is connected
      console.log("Meta Mask is Installed");
      provider = new ethers.providers.Web3Provider(window.ethereum);
      const accounts = await window.ethereum.request({method: 'eth_requestAccounts',});
      console.log(accounts);
      setConnected(true);
    }
    else{
      console.log("MetaMask extension NOT Found!!!");
    }
  }

  return (
    <div className="App">
      {connected ? <ContractPage /> : <Connection connectMetaMask={connectMetaMask}/>}
    </div>
  );
}

export default App;
