import { ethers } from "ethers";
import Conabi from "../abi/SimpleCounter.json";
import Conadd from "../abi/ContractAddress.json";
import { useState, useEffect } from "react";

const contractAbi = Conabi.abi;
const contractAddress = Conadd.address;

const ContractPage = ({ AccAddress, AccBalance }) => {

  // useEffect(() => {

  //   const storeAccounts = async (data) => {
  //     const response = await fetch("http://localhost:5000/userWallets", {
  //       method: "POST",
  //       headers: {
  //         "Content-type": "application/json"
  //       },
  //       body: JSON.stringify(data)
  //     })
  //     const result = await response.json();

  //     console.log(result);
  //   }

  //   storeAccounts({AccAddress, AccBalance});

  // }, []);

  const [updateCounter, setUpdateCounter] = useState(0);
  const [fetchedValue, setFetchedValue] = useState(0);
  const [get, setGet] = useState();

  const ContractAccess = () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner()
    const Contract = new ethers.Contract(contractAddress, contractAbi, signer);

    return Contract;
  }

  const SetCounter = async (event) => {

    event.preventDefault();

    console.log(fetchedValue);

    setUpdateCounter(fetchedValue);

    const Contract = ContractAccess();

    const res = await Contract.setCounter(fetchedValue);
    console.log(res);
    const counterUpdate = await Contract.getCount();

    console.log(counterUpdate);

    const value = ethers.utils.arrayify(get);

    console.log(value[0]);

    setUpdateCounter(value[0]);
  }

  const Increment = async () => {

    const Contract = ContractAccess();

    await Contract.incrementCounter();
    const counterUpdate = await Contract.getCount();

    setGet(counterUpdate);

    console.log(get)

    const value = ethers.utils.arrayify(get);

    console.log(value[0]);

    setUpdateCounter(value[0]);
  }

  const Decrement = async () => {

    const Contract = ContractAccess();

    await Contract.decrementCounter();
    const counterUpdate = await Contract.getCount();

    console.log(counterUpdate)

    const value = ethers.utils.arrayify(counterUpdate);

    console.log(value[0]);

    setUpdateCounter(value[0]);
  }

  return (
    <div>
      <form onSubmit={SetCounter}>
        <label>Set Counter Value
          <br />
          <input
            value={fetchedValue}
            onChange={(e) => { setFetchedValue(e.target.value) }}>
          </input>
        </label>
        <br />
        <button type="submit">Set Counter</button>
      </form>
      <div>
        {/* <button onClick={() => { setUpdateCounter(updateCounter + 1) }}>+</button> */}
        <button onClick={Increment}>+</button>
        <h2>{updateCounter}</h2>
        {/* <button onClick={() => { setUpdateCounter(updateCounter - 1) }}>-</button> */}
        <button onClick={Decrement}>-</button>
      </div>
    </div>
  )
}

export default ContractPage
