import './App.css';
import Connection from "./components/MetaMaskConnection";
import NextPage from "./components/NewPage";
import { useState } from "react";
import Web3 from 'web3';


function App() {

  const [connected, setConnected] = useState(false);

  const connectMetaMask = async () => {
    const provider = window.ethereum;
    if(provider){
      //MetaMask is connected
      console.log("Meta Mask is Installed");
      setConnected(true);
      const accounts = await provider.request({method: 'eth_requestAccounts',});
      console.log(accounts);
    }
    else{
      console.log("MetaMask extension NOT Found!!!");
    }
  }

  return (
    <div className="App">
      {connected ? <NextPage /> : <Connection connectMetaMask={connectMetaMask}/>}
    </div>
  );
}

export default App;
