import logo from './logo.svg';
import './App.css';
import Connection from "./components/MetaMaskConnection"
import Web3 from "web3"


function App() {

  const connectMetaMask = () => {
    const provider = window.ethereum;
    if(provider){
      //MetaMask is connected
      console.log("Meta Mask is Installed");
    }
    else{
      console.log("MetaMask extension NOT Found!!!");
    }
  }

  return (
    <div className="App">
      <Connection connectMetaMask={connectMetaMask}/>
    </div>
  );
}

export default App;
