import './App.css';
import Connection from "./components/MetaMaskConnection";
import ContractPage from "./components/ContractPage";
import { useState } from "react";
import { ethers } from "ethers";
// import Conabi from "./abi/SimpleCounter.json";
// import ConAdd from "./abi/ContractAddress.json";

// const contractAbi = Conabi.abi;
// const contractAddress = ConAdd.address;

function App() {

  const [connected, setConnected] = useState(false);
  const [AccAddress, setAccAddress] = useState('');
  const [AccBalance, setAccBalance] = useState('');


  const connectMetaMask = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    const accounts = await provider.send("eth_requestAccounts", []);
    setAccAddress(accounts);
    console.log(accounts);
    const balance = await provider.getBalance(accounts);
    const balanceInEther = ethers.utils.formatEther(balance);
    setAccBalance(balanceInEther);
    console.log(balance);
    setConnected(true);
  }
  return (
    <div className="App">
      {connected ? <ContractPage /> : <Connection connectMetaMask={connectMetaMask} AccAddress={AccAddress} AccBalance={AccBalance}/>}
    </div>
  );
}

export default App;
